template.value:
[
    'attribute' => 'created_at',
    'contentOptions' => ['style' => 'text-align: right;'],
    'content' => function($model): string {
        return $model->created_at
            ? implode('<br>', [
                Yii::$app->formatter->asDate($model->created_at),
                Html::tag('span', Yii::$app->formatter->asTime($model->created_at), ['class' => 'text-muted']),
            ])
            : Yii::$app->formatter->nullDisplay;
    },
],

[
    'attribute' => 'user_id',
    'contentOptions' => ['style' => 'width: 16px; text-align: right;'],
    'content' => function($model): string {
        $user = User::findOne($model->user_id);
        if (!$user) {
            return Yii::$app->formatter->nullDisplay;
        }
        return implode('<br>', [
            Html::tag(
                'span',
                $user->profile->full_name,
                [
                    'style' => 'display: inline-block; max-width: 128px; overflow: hidden; text-overflow: ellipsis; white-space: nowrap;',
                    'title' => $user->profile->full_name,
                ]
            ),
            Html::tag(
                'small',
                '#'.$user->id.' '.$user->email,
                [
                    'class' => 'text-muted',
                    'style' => 'display: inline-block; max-width: 128px; overflow: hidden; text-overflow: ellipsis; white-space: nowrap;',
                    'title' => '#'.$user->id.' '.$user->email,
                ]
            ),
        ]);
    },
],

[
    'attribute' => 'old_value',
    'contentOptions' => ['style' => 'white-space: normal;'],
    'content' => function($model): string {
        if ($model->old_value === null) {
            return Yii::$app->formatter->nullDisplay;
        }

        $html = [StringHelper::truncate($model->old_value, 48, '…')];

        if (mb_strlen($model->old_value) > 48) {
            ob_start();
            Modal::begin([
                'header' => 'Просмотр',
                'toggleButton' => ['label' => 'Просмотр', 'class' => 'btn btn-default btn-sm'],
            ]);
            echo '<pre><code>'.$model->old_value.'</code></pre>';
            Modal::end();
            $html[] = ob_get_contents();
            ob_get_clean();
        }

        return implode('', $html);
    },
],
